        char y[2] = { a1, a2 };
        INT16 * b = (INT16*)y;
        second = 2 * (*b & 31);
        minute = (*b & 2016) >> 5;
        hour = (*b & 63488) >> 11;
        data = *b;
    }
    DosTime(){
        hour = 0;
        minute = 0;
        second = 0;
    }
};
struct Frame{
    char SoundOffset;
    char precisionOffset;
    char DosTime[2];
};
struct ConvertedFrame{
    float soundDB;
    DosTime time;
    void print(){
        cout << "Sound Level: ";
        cout << soundDB;
        if ((((int)soundDB) - soundDB) == 0) cout << ".0";
        cout << " dB @ ";
        cout.fill('0'); cout.width(2); cout << time.hour;
        cout << ":";
        cout.fill('0'); cout.width(2); cout << time.minute;
        cout << ":";
        cout.fill('0'); cout.width(2); cout << time.second;
